"use strict";(self.webpackChunkonigiri=self.webpackChunkonigiri||[]).push([[8130],{7735:n=>{n.exports=JSON.parse('{"archive":{"blogPosts":[{"id":"i-wanna-check-my-etc-charge","metadata":{"permalink":"/blog/i-wanna-check-my-etc-charge","source":"@site/blog/japan-lifelog/2024-06-26-i-wanna-check-my-etc-charge.md","title":"ETC \uce74\ub4dc \uc0ac\uc6a9 \uc694\uae08\uc740 \uc5b8\uc81c \uc815\uc0b0\ub418\ub098","description":"\x3c!--","date":"2024-06-26T00:00:00.000Z","tags":[{"inline":true,"label":"onigiri","permalink":"/blog/tags/onigiri"},{"inline":true,"label":"blog","permalink":"/blog/tags/blog"},{"inline":true,"label":"japan-lifelog","permalink":"/blog/tags/japan-lifelog"},{"inline":true,"label":"etc charge","permalink":"/blog/tags/etc-charge"},{"inline":true,"label":"drive","permalink":"/blog/tags/drive"},{"inline":true,"label":"\uc77c\ubcf8\uc0dd\ud65c","permalink":"/blog/tags/\uc77c\ubcf8\uc0dd\ud65c"},{"inline":true,"label":"\ud574\uc678\uc0b4\uc774","permalink":"/blog/tags/\ud574\uc678\uc0b4\uc774"},{"inline":true,"label":"\uc6b4\uc804","permalink":"/blog/tags/\uc6b4\uc804"}],"hasTruncateMarker":false,"authors":[{"name":"Onigiri.J","title":"Software Engineer","url":"https://github.com/jay2u8809","imageURL":"/img/onigiri_icon.jpg","key":"jian"}],"frontMatter":{"slug":"i-wanna-check-my-etc-charge","title":"ETC \uce74\ub4dc \uc0ac\uc6a9 \uc694\uae08\uc740 \uc5b8\uc81c \uc815\uc0b0\ub418\ub098","authors":["jian"],"tags":["onigiri","blog","japan-lifelog","etc charge","drive","\uc77c\ubcf8\uc0dd\ud65c","\ud574\uc678\uc0b4\uc774","\uc6b4\uc804"]},"unlisted":false,"nextItem":{"title":"\ub098 \ube7c\uace0 \ub2e4 \uc544\ub294 \uc77c\ubcf8\uc5b4","permalink":"/blog/japanese-word-everyone-knows-except-me"}},"content":"\x3c!--title --\x3e\\n# ETC \uce74\ub4dc\ub97c \uc0ac\uc6a9 \uc694\uae08\uc774 \uce74\ub4dc \uc694\uae08\uc5d0 \ubc18\uc601 \ub418\uc9c0 \uc54a\uc558\ub2e4\uba74?\\n\x3c!--//title --\x3e\\n\\n\x3c!-- \\n```json\\n{\\n  \\"author\\": \\"Onigiri.J\\",\\n  \\"createdAt\\": \\"2024-06-26\\",\\n  \\"updatedAt\\": \\"2024-06-27\\"\\n}\\n``` \\n--\x3e\\n\\n```yaml\\n  author: Onigiri.J\\n  createdAt: 2024-06-26\\n  updatedAt: 2024-06-27\\n```\\n\\n---\\n\\n## Intro\\n \uc77c\ubcf8\uc5d0\uc11c \uc6b4\uc804 \uba74\ud5c8\ub97c \ubc14\uafb8\uace0 \ub098\uc11c\ub294 \uaf64\ub098 \uc790\uc8fc \uc6b4\uc804\uc744 \ud558\uace0 \uc788\ub2e4. \uc544\uc9c1\uc740 \uc790\ucc28\uac00 \uc5c6\uae30\uc5d0 \uc8fc\ub85c Orix Car Share(\u30aa\u30ea\u30c3\u30af\u30b9\u30ab\u30fc\u30b7\u30a7\u30a2)\ub97c \ud1b5\ud574 \uba87 \uc2dc\uac04\uc529 \ube4c\ub824\uc11c \ub2e4\ub2cc\ub2e4.\\n\\n\uc5bc\ub9c8 \uc804 \ucf54\uc2a4\ud2b8\ucf54\ub97c \uac14\ub2e4\uc624\uba74\uc11c ETC \uce74\ub4dc\ub97c \ucc98\uc74c \uc0ac\uc6a9\ud574 \ubcf4\uac8c \ub418\uc5c8\ub2e4. \ub124\ube44\uac8c\uc774\uc158\uc5d0 \ub098\uc624\ub294 \uc608\uc0c1 \uc694\uae08\uc740 \uc57d 800\uc5d4. \uc720\ub8cc \ub3c4\ub85c\uc5d0 \uc9c4\uc785\ud558\ub2c8 ETC \uce74\ub4dc\ub97c \ub123\uc740 \ucabd\uc5d0\uc11c \\"\uc090\ube45\\" \ud558\ub294 \uc18c\ub9ac\uac00 \ub098\uba70 \uc791\ub3d9\ud588\uace0 \ubb34\uc0ac\ud788 \uc9d1\uae4c\uc9c0 \ub3cc\uc544\uc654\ub2e4.\\n\\nETC \uce74\ub4dc\uc758 \uc0ac\uc6a9 \uc694\uae08\uc740 \uc2e0\uc6a9\uce74\ub4dc \uacb0\uc81c \ub0b4\uc5ed\uc5d0 \ud568\uaed8 \ub098\uc628\ub2e4\uace0 \uc54c\uace0 \uc788\uc5c8\uae30\uc5d0 \ub2e4\uc74c\ub0a0 \ubc14\ub85c \ud655\uc778\ud574\ubd24\ub294\ub370 ETC \uce74\ub4dc \uc0ac\uc6a9 \ub0b4\uc5ed\uc740 \ubcf4\uc774\uc9c0 \uc54a\uc558\ub2e4. \uadf8 \ub2e4\uc74c\ub0a0\ub3c4, \ub2e4\ub2e4\uc74c\ub0a0\ub3c4. \uc57d 20\uc77c\uc774 \uc9c0\ub098\ub3c4 ETC \uce74\ub4dc \uc0ac\uc6a9 \uc694\uae08 \ub098\uc624\uc9c0 \uc54a\uc558\ub2e4. \\n\\n\ub300\uccb4 ETC \uce74\ub4dc \uc0ac\uc6a9 \uc694\uae08\uc740 \uc5b8\uc81c \ud655\uc778\ud560 \uc218 \uc788\ub294\uac70\uc9c0?\\n\\n\\n\\n## Contents\\n\\n### - ETC \uc0ac\uc6a9 \uc694\uae08\uc758 \uc815\uc0b0\uc77c\uc740 \ubc1c\uae09 \ud68c\uc0ac\ub9c8\ub2e4 \ub2e4\ub974\ub2e4?\\n\u3000ETC \uce74\ub4dc\ub294 \uc5ec\ub7ec \uc740\ud589\uc774\ub098 \uae08\uc6a9 \uad00\ub828 \ud68c\uc0ac\ub97c \ud1b5\ud574 \ub9cc\ub4e4 \uc218 \uc788\ub2e4. \ubc1c\uae09 \ubc1b\uc744 \uc218 \uc788\ub294 \ud68c\uc0ac\ub4e4\uc774 \ub2e4\uc591\ud558\ub2e4 \ubcf4\ub2c8 \uc815\uc0b0\uc77c\ub3c4 \uc81c\uac01\uac01\uc774\ub2e4.  \uc0ac\uc6a9\ud55c \ub2f9\uc6d4\uc758 10\uc77c, 18\uc77c, 25\uc77c \ub610\ub294 \ub2e4\uc74c \ub2ec 1\uc77c \ub4f1.\\n \\n> **ETC\u30ab\u30fc\u30c9\u7de0\u3081\u65e5** \ub098 **ETC\u30ab\u30fc\u30c9\u652f\u6255\u3044\u65e5** \ub97c \uac80\uc0c9\ud574\ubcf4\uba74 ETC \uce74\ub4dc\ub97c \ubc1c\uae09\ud55c \uc5ec\ub7ec \ud68c\uc0ac\ub4e4\uc774 \uc815\uc0b0\uc77c\uc744 \uc548\ub0b4\ud558\ub294 \ud398\uc774\uc9c0\uac00 \ub098\uc628\ub2e4.\\n \\n \ub0b4\uac00 \ubc1c\uae09\ubc1b\uc740 \ud68c\uc0ac\uc758 Q&A \ud398\uc774\uc9c0\uc5d0\uc11c \uaca8\uc6b0 ETC \uc0ac\uc6a9 \uc694\uae08 \uc815\uc0b0\uc77c\uc744  \uc54c\uc544 \ub0bc \uc218 \uc788\uc5c8\ub2e4. \ubb38\uc81c\ub294 \uc774\ubbf8 \uadf8 \uc815\uc0b0\uc77c\uc744 \uc9c0\ub0ac\ub2e4\ub294 \uc0ac\uc2e4. \uc815\uc0b0\uc77c\uc774 \uc9c0\ub0ac\ub294\ub370\ub3c4 \uc0ac\uc6a9\ub0b4\uc5ed\uc740 \uc5ec\uc804\ud788 \ub098\uc624\uc9c0 \uc54a\uace0 \uc788\uc5c8\ub2e4.\\n\\n\\n###  - \uc815\uc0b0\uc77c\uacfc \uc0ac\uc6a9 \ub0b4\uc5ed\uc5d0 \ubc18\uc601\ub418\ub294 \uc2dc\uac04\uc740 \ucc28\uc774\uac00 \uc788\ub2e4?\\n \ubb34\uc791\uc815 ETC \uce74\ub4dc \uc0ac\uc6a9 \ub0b4\uc5ed\uc774 \ub098\uc624\uae38 \uae30\ub2e4\ub9ac\uace0 \uc788\uc5c8\ub294\ub370 \uc57d 23\uc77c\uc774 \uc9c0\ub09c \ub0a0 ETC \uce74\ub4dc \uc0ac\uc6a9 \ub0b4\uc5ed\uc774 \ub4dc\ub514\uc5b4 \ub098\uc654\ub2e4. \ub0b4\uac00 \uc4f4 \uacb0\uc81c \ub0b4\uc5ed\uc744 \ubcf4\ub294\uac8c \uc774\ub807\uac8c \uc624\ub798 \uac78\ub9b4 \uc904\uc774\uc57c... \\n \\n\ub300\uccb4 \uc65c \uc774\ub807\uac8c \ub2a6\uac8c \ubc18\uc601\ub418\ub098 \ud558\uace0 \ucc3e\uc544\ubcf4\ub2c8 ETC \uce74\ub4dc \uc0ac\uc6a9 \uc694\uae08\uc740 \uac01 \ud68c\uc0ac\uc758 \uc0ac\uc6a9 \ub0b4\uc5ed\uc73c\ub85c \ubc18\uc601\ub418\uae30\uae4c\uc9c0 \ucd5c\ub300 2\ub2ec \uc815\ub3c4\ub3c4 \uac78\ub9b4\uc218 \uc788\ub2e4\ub294 \uac83\uc744 \ubcf4\uc558\ub2e4. \uc624\ub298 \uc4f4\uac78 2\ub2ec \ub4a4\uc5d0\ub098 \uc54c\uc218 \uc788\ub2e4\ub2c8. \uadf8\ub098\ub9c8 20\uc77c \uc815\ub3c4\uc5d0 \ud655\uc778\ud560 \uc218 \uc788\ub294\uac74 \ub2e4\ud589\uc778\uac74\uac00 \uc2f6\uc5c8\ub2e4.(\ucd5c\ub300 2\ub2ec \uc815\ub3c4\uace0 \ubcf4\ud1b5\uc740 1\ub2ec\uc548\uc5d0 \ucc98\ub9ac\ub418\ub294 \ub4ef\ud558\ub2e4.)\\n\\n\uc5b4\ucc0c \ub410\ub4e0 ETC \uce74\ub4dc\uac00 \uc798 \uc791\ub3d9\ud558\uace0 \uc815\uc0b0\ub3c4 \uc798 \ub418\ub294 \uac83\uc744 \ud655\uc778\ud588\uc73c\ub2c8 \uc774\uc81c \ud3b8\uc548\ud558\uac8c \uc4f8 \uc218 \uc788\uaca0\ub2e4. \ucc98\uc74c \uc0ac\uc6a9\ud588\uae30\uc5d0 \uc81c\ub300\ub85c \ub3d9\uc791\ud558\ub294\uc9c0 \uad81\uae08\ud574\uc11c \uc774\ub807\uac8c \uc624\ub9e4\ubd88\ub9dd \uae30\ub2e4\ub838\uc9c0 \uc774\uc81c\ub294 \ub531\ud788 \uae30\ub2e4\ub9b4 \uc77c\uc740 \uc5c6\uc744 \ub4ef \ud558\ub2e4.\\n\\n\\n### - ETC \uc0ac\uc6a9\uc744 \uc870\ud68c\ud560 \uc218 \uc788\ub294 \uc11c\ube44\uc2a4 \\n \uc774\ubc88 \uae30\ud68c\uc5d0 \uc54c\uac8c \ub41c ETC \uce74\ub4dc \uad00\ub828 \uc0ac\uc774\ud2b8\uac00 \uc788\ub2e4. \\n \\nETC \uce74\ub4dc\ub97c \uc0ac\uc6a9\ud55c \uc774\ub825\uc744 \ud655\uc778\ud560 \uc218 \uc788\ub294 \uc0ac\uc774\ud2b8\uc778\ub370, \uc694\uae08\ub3c4 \uc870\ud68c\uac00 \uac00\ub2a5\ud55c\uc9c0\ub294 \ubaa8\ub974\uc9c0\ub9cc \uc5b4\ub290 \uc778\ud130\uccb4\uc778\uc9c0\uc5d0\uc11c \uc2dc\uc791\ud574\uc11c \uc5b4\ub514\ub85c \ub098\uc654\ub294\uc9c0 \ub4f1 ETC \uce74\ub4dc\ub97c \uc0ac\uc6a9\ud55c \uc774\ub825\uc744 \uc54c\uc544\ubcfc \uc218 \uc788\ub2e4\uace0 \ud55c\ub2e4. \\n \\n\ub2e4\ub9cc \ucc28\ub7c9\uc758 \uc815\ubcf4\uac00 \ud544\uc694\ud55c\ub370 \uc790\ub3d9\ucc28 \ubc88\ud638\ud310\uc758 \ubc88\ud638\uac00 \uc544\ub2c8\ub77c \ub4f1\ub85d \ubc88\ud638 \uac19\uc740 \uad6c\uccb4\uc801\uc778 \uc815\ubcf4\uac00 \ud544\uc694\ud588\ub2e4. \ub098\ub294 \uce74\uc250\uc5b4 \uc11c\ube44\uc2a4\ub97c \uc774\uc6a9\ud574\uc11c \ube4c\ub838\uae30\uc5d0 \uc774 \uc11c\ube44\uc2a4\ub97c \uc774\uc6a9\ud560 \uc218\ub294 \uc5c6\uc5c8\uc9c0\ub9cc, \ub098\uc911\uc5d0\ub77c\ub3c4 \ud544\uc694\ud560 \uc77c\uc774 \uc788\uc744\uac70 \uac19\uc544 \uc990\uaca8\ucc3e\uae30 \ud574\ub450\uc5c8\ub2e4. \\n(\uce74\uc250\uc5b4 \uc11c\ube44\uc2a4 \ud68c\uc0ac\uc5d0 \ubd80\ud0c1\ud558\uba74 \uc54c\ub824\uc8fc\ub294 \uc815\ubcf4\ub77c\uace0\ub294 \ud558\ub294\ub370 \uadf8\ub807\uac8c\uae4c\uc9c0 \ud558\uace0 \uc2f6\uc9c0\ub294 \uc54a\uc558\ub2e4.)\\n\\n\\n\\n## Conclusion \\n ETC \uce74\ub4dc \uc0ac\uc6a9 \ub0b4\uc5ed\uc740 \uae30\ub2e4\ub9ac\uace0 \uc788\uc73c\uba74 \uc5b8\uc820\uac00 \ud655\uc778\ud560 \uc218 \uc788\ub2e4. \\n\\n\\n\\n<br /><br /><br /><br /><br />\\n\\n--- \\n- Refs\\n  + [ETC\u5229\u7528\u7167\u4f1a\u30b5\u30fc\u30d3\u30b9](https://www.etc-meisai.jp/)\\n  + [\uff25\uff34\uff23\u5229\u7528\u7167\u4f1a\u30b5\u30fc\u30d3\u30b9\u4e8b\u52d9\u5c40](https://www.go-etc.jp/deal/history.html)\\n  + [\u304a\u554f\u3044\u5408\u308f\u305b](https://www.go-etc.jp/contact/)"},{"id":"japanese-word-everyone-knows-except-me","metadata":{"permalink":"/blog/japanese-word-everyone-knows-except-me","source":"@site/blog/2024-06-20-japanese-word-everyone-knows-except-me.md","title":"\ub098 \ube7c\uace0 \ub2e4 \uc544\ub294 \uc77c\ubcf8\uc5b4","description":"\x3c!--","date":"2024-06-20T00:00:00.000Z","tags":[{"inline":true,"label":"onigiri","permalink":"/blog/tags/onigiri"},{"inline":true,"label":"blog","permalink":"/blog/tags/blog"},{"inline":true,"label":"japan-lifelog","permalink":"/blog/tags/japan-lifelog"},{"inline":true,"label":"japan-company","permalink":"/blog/tags/japan-company"},{"inline":true,"label":"word","permalink":"/blog/tags/word"},{"inline":true,"label":"\uc77c\ubcf8\uc0dd\ud65c","permalink":"/blog/tags/\uc77c\ubcf8\uc0dd\ud65c"},{"inline":true,"label":"\ud574\uc678\uc0b4\uc774","permalink":"/blog/tags/\ud574\uc678\uc0b4\uc774"},{"inline":true,"label":"\uc77c\ubcf8\uc5b4","permalink":"/blog/tags/\uc77c\ubcf8\uc5b4"},{"inline":true,"label":"\ud574\uc678\ucde8\uc5c5","permalink":"/blog/tags/\ud574\uc678\ucde8\uc5c5"}],"hasTruncateMarker":false,"authors":[{"name":"Onigiri.J","title":"Software Engineer","url":"https://github.com/jay2u8809","imageURL":"/img/onigiri_icon.jpg","key":"jian"}],"frontMatter":{"slug":"japanese-word-everyone-knows-except-me","title":"\ub098 \ube7c\uace0 \ub2e4 \uc544\ub294 \uc77c\ubcf8\uc5b4","authors":["jian"],"tags":["onigiri","blog","japan-lifelog","japan-company","word","\uc77c\ubcf8\uc0dd\ud65c","\ud574\uc678\uc0b4\uc774","\uc77c\ubcf8\uc5b4","\ud574\uc678\ucde8\uc5c5"]},"unlisted":false,"prevItem":{"title":"ETC \uce74\ub4dc \uc0ac\uc6a9 \uc694\uae08\uc740 \uc5b8\uc81c \uc815\uc0b0\ub418\ub098","permalink":"/blog/i-wanna-check-my-etc-charge"},"nextItem":{"title":"Intro","permalink":"/blog/Intro"}},"content":"\x3c!--title --\x3e\\n# \ub098 \ube7c\uace0 \ub2e4 \uc544\ub294 \uc77c\ubcf8\uc5b4\\n\x3c!--//title --\x3e\\n\\n\x3c!-- \\n```json\\n{\\n  \\"author\\": \\"Onigiri.J\\",\\n  \\"createdAt\\": \\"2024-06-20\\",\\n  \\"updatedAt\\": \\"2024-06-26\\"\\n}\\n``` \\n--\x3e\\n\\n```yaml\\n author: Onigiri.J\\n createdAt: 2024-06-20\\n updatedAt: 2024-06-26\\n```\\n\\n---\\n\\n> \uc77c\ubcf8\uc5d0\uc11c \uc77c\ud558\uba74\uc11c, \uc0b4\uba74\uc11c \uc54c\uac8c\ub41c \ubbf8\uccd0\ubab0\ub790\uc5c8\ub358 \uc77c\ubcf8\uc5b4\ub97c \uc815\ub9ac\ubcf8\ub2e4.\\n\\n\\n### \u9593\u53d6\u308a\\n- \ubc29\uc758 \ubc30\uce58, (\ubd80\ub3d9\uc0b0) \ud3c9\uba74\ub3c4. \\n\\n \uc8fc\ub85c \ubd80\ub3d9\uc0b0\uc5d0\uc11c \uc9d1\uc744 \uc18c\uac1c\ud560\ub54c \ud3c9\uba74\ub3c4\ub97c \ubcf4\uc5ec\uc8fc\ub294\ub370 \uadf8 \ud3c9\uba74\ub3c4\ub97c \u9593\u53d6\u308a\ub77c\uace0 \ud55c\ub2e4.\\nUR \ubc29\uc744 \uad6c\ud560 \ub54c, \uc870\uac74\uc5d0 \ub9de\ub294 \ubc29\uc774 \ub098\uc624\uba74 UR \uc0ac\ubb34\uc2e4\uc5d0\uc11c \uc804\ud654\uac00 \uc62c \ub54c\ub9c8\ub2e4 \u300c\u30e1\u30fc\u30eb\u3067\u9593\u53d6\u308a\u3082\u9001\u3063\u3066\u304f\u3060\u3055\u3044\u3002\u300d\ub77c\uace0 \ud558\uba70 \ubc1b\uc558\ub358 \uae30\uc5b5\uc774 \uc788\ub2e4.\\n\\n\\n### \u524d\u64ae\u308a\\n- (\uacb0\ud63c\uc2dd \uc804\uc5d0 \ucd2c\uc601\ud558\ub294)\uc6e8\ub529\ucd2c\uc601.\\n\\n \uacb0\ud63c\uc2dd \uc900\ube44\uc5d0\uc11c \ube60\uc9c8 \uc218 \uc5c6\ub294 \uac83\uc774 \uc6e8\ub529\ucd2c\uc601\uc774\ub2e4. \u524d\u64ae\u308a\ub294 \uacb0\ud63c\uc2dd\uc774\ub098 \ud589\uc0ac \uc804\uc5d0 \uae30\ub150 \uc0ac\uc9c4\uc744 \ucd2c\uc601\ud558\ub294 \uac83\uc73c\ub85c \ud55c\uad6d\uc758 \uacb0\ud63c\uc2dd\uc758 \uc2a4\ub4dc\uba54 \uc911 \uc2a4\ud29c\ub514\uc624 \ucd2c\uc601\uc5d0 \ud574\ub2f9\ud55c\ub2e4.(\uc2a4\ud29c\ub514\uc624\uc5d0\uc11c\uc758 \ucd2c\uc601\uc744 \uc758\ubbf8\ud55c\ub2e4\uae30 \ubcf4\ub2e4 \uc2dd \uc774\uc804\uc5d0 \ucd2c\uc601\ud55c\ub2e4\ub294 \uc758\ubbf8)\\n\\n\\n### \u7d44\u8fbc\u307f\u30d7\u30ed\u30b0\u30e9\u30df\u30f3\u30b0\\n- \uc784\ubca0\ub514\ub4dc(Embedded) \ud504\ub85c\uadf8\ub798\ubc0d\\n\\n \ud68c\uc0ac\uc5d0 \uacbd\ub825\uc9c1\uc73c\ub85c \uc785\uc0ac\ud558\uc2e0 \ubd84\uc758 \uc790\uae30\uc18c\uac1c\ub97c \ub4e4\uc73c\uba70 \ucc98\uc74c \uc54c\uac8c\ub418\uc5c8\ub2e4. \uc784\ubca0\ub514\ub4dc\ub780 \ub9d0\ubcf4\ub2e4 \ub354 \uc790\uc8fc \uc0ac\uc6a9\ud558\uc9c0\ub294 \ubaa8\ub974\uaca0\uc73c\ub2c8 \uadf8 \ubd84\uc774 \uc804 \uc9c1\uc7a5\uc5d0\uc11c\ub294 \u7d44\u8fbc\u307f\u30a8\u30f3\u30b8\u30cb\u30a2\ub85c\uc11c \uc77c\ud558\uc168\ub2e4\uae30\uc5d0 \ubabb\uc54c\uc544\ub4e3\uace0 \ucc3e\uc544\ubcf4\uc558\ub2e4.\\n\\n### \u3057\u304d\u3044\u5024\uff08\u95be\u5024\uff09\uff1a\u3057\u304d\u3044\u3061\\n- \uc784\uacc4\uac12, \uc5ed\uce58, Absolute threshold\\n\\n \uc8fc\ub85c \uc11c\ubc84 \ubaa8\ub2c8\ud130\ub9c1\ud560 \ub54c \uc790\uc8fc \ub4e4\uc740 \ub2e8\uc5b4\uc774\ub2e4. \uc5d0\ub7ec\uac00 \uc77c\uc815\ube44\uc728\uc774\ub098 \uc77c\uc815 \ud69f\uc218 \uc774\uc0c1 \ubc1c\uc0dd\ud560 \uacbd\uc6b0, \uacbd\ubcf4(Alert)\uba54\uc2dc\uc9c0\uac00 \uc624\ub3c4\ub85d \uc124\uc815\ub418\uc5b4\uc788\ub294\ub370 \uadf8 \uc77c\uc815\ube44\uc728\uc774\ub098 \ud69f\uc218\ub97c \u3057\u304d\u3044\u5024\ub77c\uace0 \ud55c\ub2e4. \u300c\u3053\u306e\u30a2\u30e9\u30fc\u30c8\u306e\u3057\u304d\u3044\u5024\u306f\u5e73\u574710\uff05\u3067\u8a2d\u5b9a\u3057\u307e\u3057\u305f\u3002\u300d\ub77c\ub294 \uc2dd\uc73c\ub85c \uc0ac\uc6a9\ud558\uace0 \uc788\ub2e4.\\n \\n### \u30d9\u30bf\u66f8\u304d\\n- Hard coding, \ud558\ub4dc\ucf54\ub529\\n\\n \ucf54\ub4dc\ub97c \uc791\uc131\ud560 \ub54c, \ub370\uc774\ud130\ub97c \ucf54\ub4dc\uc5d0 \uadf8\ub300\ub85c \ub123\ub294(\ubcc0\uc218\ub97c \uc774\uc6a9\ud558\uc9c0 \uc54a\uace0) \ud558\ub4dc\ucf54\ub529\uc744 \uc758\ubbf8\ud55c\ub2e4. \\n\u300c\u30c6\u30b9\u30c8\u30b3\u30fc\u30c9\u306f\u30d9\u30bf\u66f8\u304d\u3067\u3082\u826f\u3055\u305d\u3046\u3067\u3059\u3002\u300d \ub77c\ub294 \ub9ac\ubdf0\ub97c \ubc1b\uc740\uc801\uc774 \uc788\ub2e4.\\n\\n### \u9000\u793e\\n- \ud1f4\uadfc\ud558\ub2e4.\\n\\n \uc77c\ubcf8\uc5d0\uc11c \uc77c\ud558\uba74\uc11c \ucc98\uc74c\uc5d0 \uac00\uc7a5 \uc5b4\uc0c9\ud587\ub358 \ub2e8\uc5b4\uc774\ub2e4. \ud55c\uad6d\uc5d0\uc11c \\"\ud1f4\uc0ac\\" \ub77c\ud568\uc740 \ud68c\uc0ac\ub97c \uadf8\ub9cc\ub450\ub294 \uc758\ubbf8\uac00 \uc788\uc9c0\ub9cc, \uc77c\ubcf8\uc5d0\uc11c\ub294 \\"\ud1f4\uadfc\\" \uc758 \uc758\ubbf8\uac00 \uc788\ub2e4. (\ud55c\uad6d\uacfc \ub9c8\ucc2c\uac00\uc9c0\ub85c \ud68c\uc0ac\ub97c \uadf8\ub9cc\ub454\ub2e4\ub294 \ub73b\uc774 \uc788\ub294\uc9c0\ub294 \uc798 \ubaa8\ub974\uaca0\ub2e4.)\\n\ubb3c\ub860 \u9000\u52e4\uc774\ub77c\ub294 \ub9d0\ub3c4 \uac19\uc774 \uc4f0\uae34 \ud558\ub358\ub370 \uc8fc\ub85c \u51fa\u793e\u3001\u9000\u793e\ub85c \ucd9c\uadfc\uacfc \ud1f4\uadfc\uc744 \ub9d0\ud558\uae30\uc5d0 \uc9c0\uae08\uc740 \ub098\ub3c4 \uc790\uc5f0\uc2a4\ub7fd\uac8c \u51fa\u793e\u3057\u307e\u3057\u305f \ub77c\ub358\uac00 \u9000\u793e\u3057\u307e\u3057\u305f\u3002\ub77c\ub294 \ub9d0\uc744 \uc4f0\uace0 \uc788\ub2e4. \ucc38\uace0\ub85c \ud68c\uc0ac\ub97c \uadf8\ub9cc\ub458\ub54c\ub294 \u9000\u8077 \ub77c\ub294 \ub9d0\uc744 \uc8fc\ub85c \uc4f0\ub294 \ub4ef\ud558\ub2e4.\\n\\n\\n<br /><br /><br /><br /><br />"},{"id":"Intro","metadata":{"permalink":"/blog/Intro","source":"@site/blog/index.md","title":"Intro","description":"Welcome Onigiri Blog","date":"2024-03-31T14:08:32.000Z","tags":[{"inline":true,"label":"onigiri","permalink":"/blog/tags/onigiri"},{"inline":true,"label":"blog","permalink":"/blog/tags/blog"}],"hasTruncateMarker":false,"authors":[{"name":"Onigiri.J","title":"Software Engineer","url":"https://github.com/jay2u8809","imageURL":"/img/onigiri_icon.jpg","key":"jian"}],"frontMatter":{"slug":"Intro","title":"Intro","authors":["jian"],"tags":["onigiri","blog"]},"unlisted":false,"prevItem":{"title":"\ub098 \ube7c\uace0 \ub2e4 \uc544\ub294 \uc77c\ubcf8\uc5b4","permalink":"/blog/japanese-word-everyone-knows-except-me"}},"content":"Welcome Onigiri Blog"}]}}')}}]);